### C++ Program: Word Counter

```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>
#include <cctype>
#include <algorithm>

// Function to convert a string to lowercase
std::string toLower(const std::string& str) {
    std::string lowerStr = str;
    std::transform(lowerStr.begin(), lowerStr.end(), lowerStr.begin(),
                   [](unsigned char c) { return std::tolower(c); });
    return lowerStr;
}

// Function to strip punctuation from a word
std::string stripPunctuation(const std::string& word) {
    std::string strippedWord;
    for (char c : word) {
        if (!std::ispunct(c)) {
            strippedWord += c;
        }
    }
    return strippedWord;
}

// Function to count the occurrences of a word in a line
int countWordInLine(const std::string& line, const std::string& word) {
    std::istringstream iss(line);
    std::string temp;
    int count = 0;

    while (iss >> temp) {
        std::string cleanedWord = stripPunctuation(temp);
        if (toLower(cleanedWord) == toLower(word)) {
            count++;
        }
    }

    return count;
}

int main() {
    std::string filename, wordToCount;
    int totalOccurrences = 0;

    // Ask the user for the filename and the word to count
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

