        std::cerr << "Error opening file: " << filename << std::endl;
        return 1;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        totalOccurrences += countWordInLine(line, wordToCount);
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word '" << wordToCount << "' occurs " << totalOccurrences << " times in the file." << std::endl;

    return 0;
}
```

