import java.util.HashMap;
import java.util.Map;

public class SpringConfigHelper {

    private static final String concode_field_sep = ".";
    private static final String concode_elem_sep = ",";

    public static class SpringConfig {
        private double tension;
        private double friction;
        private String placeholderType;
        private String placeHolder;

        public SpringConfig(double tension, double friction, String placeholderType, String placeHolder) {
            this.tension = tension;
            this.friction = friction;
            this.placeholderType = placeholderType;
            this.placeHolder = placeHolder;
        }

        public double getTension() {
            return tension;
        }

        public double getFriction() {
            return friction;
        }

        public String getPlaceholderType() {
            return placeholderType;
        }

        public String getPlaceHolder() {
            return placeHolder;
        }

        @Override
        public String toString() {
            return "SpringConfig{" +
                    "tension=" + tension +
                    ", friction=" + friction +
                    ", placeholderType='" + placeholderType + '\'' +
                    ", placeHolder='" + placeHolder + '\'' +
                    '}';
        }
    }

    public static SpringConfig createSpringConfig(double tension, double friction, String placeholderType, String placeHolder) {
        return new SpringConfig(tension, friction, placeholderType, placeHolder);
    }

    public static SpringConfig defaultConfig() {
        return createSpringConfig(1.0, 0.1, "default", "placeholder");
    }

    public static SpringConfig parseSpringConfig(String configString) {
        String[] fields = configString.split(concode_field_sep);
        Map<String, String> fieldMap = new HashMap<>();
        for (String field : fields) {
            String[] parts = field.split(concode_elem_sep);
            if (parts.length == 2) {
                fieldMap.put(parts[0].trim(), parts[1].trim());
            }
        }

        double tension = Double.parseDouble(fieldMap.getOrDefault("tension", "1.0"));
        double friction = Double.parseDouble(fieldMap.getOrDefault("friction", "0.1"));
        String placeholderType = fieldMap.getOrDefault("placeholderType", "default");
        String placeHolder = fieldMap.getOrDefault("placeHolder", "placeholder");

        return createSpringConfig(tension, friction, placeholderType, placeHolder);
    }

    public static void main(String[] args) {
        // Example usage of defaultConfig
        SpringConfig defaultConfig = defaultConfig();
        System.out.println("Default Config: " + defaultConfig.toString());

        // Example usage of parseSpringConfig
        String configString = "tension=2.0.friction=0.5.placeholderType=custom.placeHolder=customPlaceholder";
        SpringConfig parsedConfig = parseSpringConfig(configString);
        System.out.println("Parsed Config: " + parsedConfig.toString());
    }
}
```

