import java.io.IOException;
import java.io.InputStream;
import java.util.Properties;

public class RegularExpressionFetcher {
    private static final String EMPTY_STRING = "";
    private static final String concode_field_sep = ",";
    private static final String concode_elem_sep = ";";
    private static final String regularExpPropertyFileLocation = "regularexp.properties";
    private static final String messagePropertyFileLocation = "messages.properties";

    private Properties regularExpproperties;
    private Properties messagesProperties;

    public RegularExpressionFetcher() {
        regularExpproperties = new Properties();
        messagesProperties = new Properties();

        try {
            // Load regular expression properties
            InputStream regularExpInputStream = getClass().getResourceAsStream(regularExpPropertyFileLocation);
            if (regularExpInputStream != null) {
                regularExpproperties.load(regularExpInputStream);
                regularExpInputStream.close();
            } else {
                System.err.println(getMessageValue("error.loading.regularexp.properties"));
            }

            // Load messages properties
            InputStream messageInputStream = getClass().getResourceAsStream(messagePropertyFileLocation);
            if (messageInputStream != null) {
                messagesProperties.load(messageInputStream);
                messageInputStream.close();
            } else {
                System.err.println(getMessageValue("error.loading.messages.properties"));
            }
        } catch (IOException e) {
            System.err.println(getMessageValue("error.loading.properties"));
            e.printStackTrace();
        }
    }

    public String getRegularExpression(String key) {
        return regularExpproperties.getProperty(key, EMPTY_STRING);
    }

    public String getMessageValue(String key) {
        return messagesProperties.getProperty(key, EMPTY_STRING);
    }

    public static void main(String[] args) {
        RegularExpressionFetcher fetcher = new RegularExpressionFetcher();
        String key = "exampleKey"; // Replace with the actual key you want to fetch
        String regex = fetcher.getRegularExpression(key);
        System.out.println("Regular Expression for key '" + key + "': " + regex);

        // Example of fetching a message value
