        this.fMethod = method;
        this.fMethodSignature = method.getSignature();
        this.fMethodName = method.getElementName(); // Use getElementName to get the method name
        this.fIsConstructor = isConstructor();
        this.LENGTH = calculateLENGTH();
    }

