        return fMethodName.equals("<init>");
    }

    private int calculateLENGTH() {
        String[] parts = fMethodSignature.split("\\("); // Split by opening parenthesis
        String params = parts[1].split("\\)")[0]; // Get the part before the closing parenthesis
        return params.length();
    }

