import java.security.NoSuchAlgorithmException;
import java.security.cert.CertificateException;
import java.util.Arrays;
import java.util.List;

public class CustomSSLConnectionSocketFactory {

    private static final String CACERTS_FILE = "cacerts";
    private static final String SSL_PROTOCOL = "TLS";
    private static final String[] SUPPORTED_PROTOCOLS = {"TLSv1.1", "TLSv1.2", "TLSv1.3"};
    private static final String[] SUPPORTED_CIPHER_SUITES = {
        "TLS_ECDHE_RSA_WITH_AES_128_GCM_SHA256",
        "TLS_ECDHE_ECDSA_WITH_AES_128_GCM_SHA256",
        "TLS_RSA_WITH_AES_128_GCM_SHA256",
        "TLS_RSA_WITH_AES_128_CBC_SHA"
    };
    private static final X509HostnameVerifier STRICT_HOSTNAME_VERIFIER = new StrictHostnameVerifier();
    private static final X509HostnameVerifier ALLOW_ALL_HOSTNAME_VERIFIER = SSLSocketFactory.ALLOW_ALL_HOSTNAME_VERIFIER;
    private static final X509HostnameVerifier BROWSER_COMPATIBLE_HOSTNAME_VERIFIER = SSLSocketFactory.BROWSER_COMPATIBLE_HOSTNAME_VERIFIER;
    private static final X509HostnameVerifier hostnameVerifier = STRICT_HOSTNAME_VERIFIER;
    private static final SSLSocketFactory socketFactory;

    static {
        try {
            KeyStore keyStore = KeyStore.getInstance(KeyStore.getDefaultType());
            try (FileInputStream fis = new FileInputStream(CACERTS_FILE)) {
                keyStore.load(fis, null);
            }

            TrustManagerFactory tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
            tmf.init(keyStore);

            SSLContext sslContext = SSLContext.getInstance(SSL_PROTOCOL);
            sslContext.init(null, tmf.getTrustManagers(), new SecureRandom());

            socketFactory = sslContext.getSocketFactory();
        } catch (KeyStoreException | NoSuchAlgorithmException | CertificateException | IOException | KeyManagementException e) {
            throw new RuntimeException("Failed to initialize SSL context", e);
        }
    }

    public static SSLSocketFactory getSocketFactory() {
        return socketFactory;
    }

    public static void verifyHostname(String hostname, SSLSession session) throws IOException {
        hostnameVerifier.verify(hostname, session);
    }

    public static Socket createSocket() throws IOException {
        return enableTLSOnSocket(socketFactory.createSocket());
    }

    public static Socket createLayeredSocket(Socket socket, String host, int port, boolean autoClose) throws IOException {
        return enableTLSOnSocket(socketFactory.createSocket(socket, host, port, autoClose));
    }

    public static Socket connectSocket(Socket socket, String host, int port) throws IOException {
        socket.connect(new java.net.InetSocketAddress(host, port));
        return socket;
    }

    public static X509HostnameVerifier getHostnameVerifier() {
        return hostnameVerifier;
    }

    public static SSLConnectionSocketFactory getSystemSocketFactory() {
        return new SSLConnectionSocketFactory(getSocketFactory());
    }

    public static void prepareSocket(Socket socket) throws IOException {
        if (socket instanceof SSLSocket) {
            SSLSocket sslSocket = (SSLSocket) socket;
            sslSocket.setEnabledProtocols(SUPPORTED_PROTOCOLS);
            sslSocket.setEnabledCipherSuites(SUPPORTED_CIPHER_SUITES);
        }
    }

    private static Socket enableTLSOnSocket(Socket socket) {
        if (socket instanceof SSLSocket) {
            SSLSocket sslSocket = (SSLSocket) socket;
            sslSocket.setEnabledProtocols(SUPPORTED_PROTOCOLS);
            sslSocket.setEnabledCipherSuites(SUPPORTED_CIPHER_SUITES);
        }
        return socket;
    }

    private static class StrictHostnameVerifier implements X509HostnameVerifier {
        @Override
        public void verify(String host, SSLSession session) throws IOException {
            if (!host.equalsIgnoreCase(session.getPeerHost())) {
                throw new IOException("Hostname verification failed");
            }
        }

        @Override
        public void verify(String host, X509Certificate cert) throws SSLException {
            if (!host.equalsIgnoreCase(cert.getSubjectX500Principal().getName())) {
                throw new SSLException("Hostname verification failed");
            }
        }

        @Override
        public void verify(String host, String[] cns, String[] subjectAlts) throws SSLException {
            // Implement your own logic for verifying the hostname against the certificate's Subject Alternative Names
        }

        @Override
        public boolean verify(String host, SSLSession session) {
            try {
                verify(host, session);
                return true;
            } catch (IOException e) {
                return false;
            }
        }
    }

    public static void main(String[] args) {
        try {
            CustomSSLConnectionSocketFactory socketFactory = new CustomSSLConnectionSocketFactory();
            Socket socket = socketFactory.createSocket("example.com", 443);
            // Use the socket for SSL communication
            socket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
```

